export {Wikstory}

import Diff from 'diff'

import {DataSourceInterface} from "./dataSource/DataSourceInterface"

import { 
    InvalidInputError,
    RollBackOnInitialCommit,
    ItemNotFoundError,
    IdenticalCommitError,
} from '../Errors'
import { createHash } from 'crypto';


function hashContent(content) {
    return createHash('sha1').update(content).digest('hex').toUpperCase();
}

class LineChange {
    line: number
    type: string
    hash: string

    constructor(line: number, type: string, hash: string) {
        this.line = line
        this.type = type
        this.hash = hash
    }
}

class Wikstory {
    dataStrategy

    constructor(DataStrategy){
        if (!(DataStrategy instanceof DataSourceInterface)) throw new Error("Invalid data strategy class passed to wikstory.");
        this.dataStrategy = DataStrategy;
    }

    async disconnect(){
        await this.dataStrategy.disconnect();
    }

    async commit(uri, text, userName){

        if (typeof(uri) != "string" || typeof(text) != "string" || typeof(userName) != "string") throw new InvalidInputError("Invalid input: 'uri, text, and userName' must be non-empty strings.");

        let newDict: Map<string, string> = new Map<string, string>()
        let newHashes: string[] = [];
        for (const [index, line] of text.split('\n').entries()){
            const hash = hashContent(line);
            newHashes[index] = hash;
            newDict[hash] = line;
        }

        let blobHashes: string[] = [];
        let blobLines: string[] = [];
        let lineChanges: LineChange[] = [];

        let prev = await this.dataStrategy.getFile(uri);
        let prevText = prev ? prev.file_text : null;
        let parenthash = prev ? prev.commit_hash : null;

        if (text == prevText) throw new IdenticalCommitError("New commit is identical.");

        let changes;
        if (prevText == null){
            //in the case of first commit just emulate output of diff algo
            changes = [{
                count: newHashes.length,
                added: true,
                removed: false,
                value: newHashes
            }]
        } else {
            //get the hashes for the previous lines
            let prevHashes: string[] = [];
            for (const [index, line] of prevText.split('\n').entries()){
                const hash = hashContent(line);
                prevHashes[index] = hash;
            }

            changes = Diff.diffArrays(prevHashes, newHashes);
        }

        let oldIndex = 0;
        let newIndex = 0;
        for (const change of changes){
            //no change
            if (!change.added && !change.removed){
                oldIndex += change.count;
                newIndex += change.count;
                continue;
            }
            //delete
            if (change.removed){
                for (const hash of change.value){
                    lineChanges.push(new LineChange(oldIndex, 'del', hash));
                    oldIndex++;
                }
            }
            //add
            if (change.added){
                for (const hash of change.value){
                    lineChanges.push(new LineChange(newIndex, 'add', hash));
                    newIndex++

                    //add new lines to lists
                    //doesn't matter about dupes as it just increments the dependencies tracker
                    blobHashes.push(hash);
                    blobLines.push(newDict[hash]);
                }
            }
        }

        //do validation checking here first

        const obj = {
            uri,
            lineChanges,
            parentHash: parenthash,
            author: userName,
        };
        const commitHash = hashContent(JSON.stringify(obj));

        await this.dataStrategy.saveCommit(commitHash, parenthash, lineChanges, userName);
        for (let i = 0; i < blobHashes.length; i++){
            await this.dataStrategy.saveBlob(blobHashes[i], blobLines[i]);
        }
        await this.dataStrategy.saveFile(uri, commitHash, text)

        return commitHash;
    }

    async rollback(uri){
        const fileInfo = await this.dataStrategy.getFileWithCommit(uri);

        if (!fileInfo.parent_hash){
            throw new RollBackOnInitialCommit("Cannot roll back on the first commit.");
        }

        let fileLines = fileInfo.file_text.split('\n');
        const lineChanges = JSON.parse(fileInfo.line_changes).changes;

        let deletedHashes: string[] = [];
        //for adds remove based on line number
        for (const change of lineChanges){
            if (change.type == 'add') {
                //delete relevant lines from fileInfo.file_text
                fileLines[change.line] = null;

                //delete/remove depend for blobs
                deletedHashes.push(change.hash);
            }
        }
        fileLines = fileLines.filter(item => item !== null); //remove null values
        //for deletes insert values from blobs
        for (const change of lineChanges){
            if (change.type == 'del'){
                const blob = await this.dataStrategy.getBlob(change.hash);
                fileLines.splice(change.line, 0, blob.line_text);
            }
        }

        let fileText = fileLines.join('\n');

        //update db with file
        await this.dataStrategy.saveFile(uri, fileInfo.parent_hash, fileText);
        //remove top level commit
        await this.dataStrategy.deleteCommit(fileInfo.commit_hash);
        //depricate and delete blobs
        for (const hash of deletedHashes){await this.dataStrategy.depricateBlob(hash);}
        await this.dataStrategy.trimBlobs();

        return fileInfo.parent_hash;
    }

    async rollbackToCommit(uri, hash){
        //check commit is in history
        let fileObj = await this.dataStrategy.getFile(uri);
        let checkHash = fileObj.commit_hash;
        if (checkHash == hash){
            //cant roll back to current commit
            return;
        }

        while(checkHash != hash){
            checkHash = await this.dataStrategy.getParentCommit(checkHash);
            if ( checkHash == null) {
                //error hash not in chain
                throw new ItemNotFoundError(`Could not find a commit with the hash ${hash} for the URI ${uri}.`);
            }
        }

        while(await this.rollback(uri) != hash) continue;
    }

    async rollbackUsername(uri){
        let fileCommitObj = await this.dataStrategy.getFileWithCommit(uri);
        let author = fileCommitObj.author;
        let checkAuthor = author;

        while(checkAuthor == author){
            let commitHash = await this.rollback(uri);
            checkAuthor = await this.dataStrategy.blame(commitHash);
        }
    }

    async getFileWithCommit(uri){
        if (typeof(uri) != "string") throw new InvalidInputError(`URI must be non-empty string.`);

        return await this.dataStrategy.getFileWithCommit(uri);
    }

    async getRecentCommits(num){
        return await this.dataStrategy.getRecentCommits(num);
    }

    async getCommitHistory(uri){
        if (typeof(uri) != "string") throw new InvalidInputError(`URI must be non-empty string.`);

        return await this.dataStrategy.getCommitHistory(uri);
    }

    async getParentCommit(hash){
        if (typeof(hash) != "string") throw new InvalidInputError("Hash must be non-empty String.");
        return await this.dataStrategy.getParentCommit(hash);
    }

    async renameFile(oldURI, newURI){
        if (!oldURI || !newURI) throw new Error("One of the provided URIs was empty.");

        this.dataStrategy.renameFile(oldURI, newURI);
    }

    async blame(hash){
        if (typeof(hash) != "string") throw new InvalidInputError("Hash must be non-empty String.");
        this.dataStrategy.blame(hash);
    }
}

export {DataSourceInterface};

class DataSourceInterface {
	constructor(client) {
		if (!client) throw new Error("No client passed to data source strategy.");
	}

	async disconnect() {
		throw new Error("Method 'disconnect()' must be implemented.");
	}

	async saveCommit(commitHash, parentHash, lineChanges, author) {
		throw new Error("Method 'saveCommit()' must be implemented.");
	}
	async saveBlob(hash, content) {
		throw new Error("Method 'saveBlob()' must be implemented.");
	}
	async saveFile(uri, commitHash, fileText) {
		throw new Error("Method 'saveFile()' must be implemented.");
	}

	async getFile(uri) {
		throw new Error("Method 'getFile()' must be implemented.");
	}
	async getFileWithCommit(uri) {
		throw new Error("Method 'getFileWithCommit()' must be implemented.");
	}
	async getBlob(hash) {
		throw new Error("Method 'getBlob()' must be implemented.");
	}

	async deleteCommit(commitHash){
		throw new Error("Method 'deleteCommit()' must be implemented.");
	}
	async depricateBlob(hash) {
		throw new Error("Method 'depricateBlob()' must be implemented.");
	}
	async trimBlobs() {
		throw new Error("Method 'trimBlobs()' must be implemented.");
	}
	async getParentCommit(hash) {
		throw new Error("Method 'getParentCommit()' must be implemented.");
	}

	async getRecentCommits(num) {
		throw new Error("Method 'getRecentCommits()' must be implemented.");
	}
	async getCommitHistory(uri) {
		throw new Error("Method 'getCommitHistory()' must be implemented.");
	}

	async renameFile(oldURI, newURI) {
		throw new Error("Method 'renameFile()' must be implemented.");
	}

	async blame(hash) {
		throw new Error("Method 'blame()' must be implemented.");
	}
}

export { WikstoryBuilder }

import { Wikstory } from './Wikstory'
import { MySQLStrategy } from './dataSource/MysqlStrategy'

class WikstoryBuilder{
    type
    client

    static types = {
        mysql: 'mysql',
        memory: 'memory'
    }

    withDataSource(type){
        this.type = type;
        return this;
    }

    andClient(client){
        this.client = client;
        return this;
    }

    make(){
        let dataSource;

        switch (this.type) {
            case WikstoryBuilder.types.mysql:
                if (!this.client) throw new Error("A mysql pool is required for this type.");
                dataSource = new MySQLStrategy(this.client);
                break;
            case WikstoryBuilder.types.memory:
                throw new Error("Memory data management is not implemented yet.");
                break;
            default:
                throw new Error("Invalid data source type.")
        }

        return new Wikstory(dataSource);
    }

    static createMySQLPool({
        host,
        user,
        password,
        database,
        waitForConnections,
        connectionLimit,
        queueLimit
    }){
        const {createPool} = require('mysql2/promise');

        return createPool({
            host: host,
            user: user,
            password: password,
            database: database,
            waitForConnections: waitForConnections, //true
            connectionLimit: connectionLimit, //20
            queueLimit: queueLimit, //0
        });
    }
}

export {MySQLStrategy}

import {DataSourceInterface} from './DataSourceInterface';
import {ItemNotFoundError} from '../../Errors'
import { gunzip, gzip } from 'zlib';


function compressString(inputString) {
    return new Promise((resolve, reject) => {
        gzip(inputString, (err, compressedBuffer) => {
            if (err) {
                reject(err);
            } else {
                resolve(compressedBuffer.toString('base64')); // Convert buffer to base64 for easy storage/transmission
            }
        });
    });
}

function decompressString(compressedString) {
    return new Promise((resolve, reject) => {
        const buffer = Buffer.from(compressedString, 'base64'); // Decode from base64
        gunzip(buffer, (err, decompressedBuffer) => {
            if (err) {
                reject(err);
            } else {
                resolve(decompressedBuffer.toString()); // Convert buffer back to string
            }
        });
    });
}

class MySQLStrategy extends DataSourceInterface {
    pool

    constructor(pool){
        super(pool);

        this.pool = pool;
    }

    async disconnect(){
        await this.pool.end();
    }

    async saveCommit(commitHash, parentHash, lineChanges, author){
        const query = `
            INSERT INTO commits (hash, parent_hash, line_changes, author, commit_date)
            VALUES (UNHEX(?), UNHEX(?), ?, ?, DEFAULT)
        `;
        const params = [commitHash, parentHash, JSON.stringify({changes: lineChanges}), author];

        await this.pool.execute(query, params);
    }

    async saveBlob(hash, content){

        let compressedContent = await compressString(content);

        const query = `
            INSERT INTO blobs (blob_hash, line_text)
            VALUES (UNHEX(?), ?)
            ON DUPLICATE KEY UPDATE dependencies = dependencies + 1
        `;
        const params = [hash, compressedContent];

        await this.pool.execute(query, params);
    }

    async saveFile(uri, commitHash, fileText){
            const query = `
                INSERT INTO files (uri, current_commit_hash, file_text)
                VALUES (?, UNHEX(?), ?)
                ON DUPLICATE KEY UPDATE
                    current_commit_hash = VALUES(current_commit_hash),
                    file_text = VALUES(file_text);
            `;
            const params = [uri, commitHash, fileText];
            await this.pool.execute(query, params);
    }

    async getFile(uri){
        const query = `
        SELECT uri, HEX(current_commit_hash) AS commit_hash, file_text
        FROM files
        WHERE uri = ?
        `;
        const [rows] = await this.pool.execute(query, [uri]);

        if (rows.length > 0) {
            return rows[0];
        } else {
            return null;
        }
    }

    async getFileWithCommit(uri){
        const query = `
            SELECT f.uri, HEX(f.current_commit_hash) AS commit_hash, f.file_text, DATE_FORMAT(c.commit_date, '%d/%m/%Y') AS formatted_date, c.author, HEX(c.parent_hash) AS parent_hash, c.line_changes
            FROM files f
            JOIN commits c ON f.current_commit_hash = c.hash
            WHERE f.uri = ?`;
        const [rows] = await this.pool.execute(query, [uri]);

        if (rows.length > 0){
            return rows[0];
        } else {
            throw new ItemNotFoundError(`Could not find file with URI ${uri}.`);
        }
    }

    async getBlob(hash){
        const query = `
        SELECT line_text FROM blobs
        WHERE blob_hash = UNHEX(?)`;

        const [rows] = await this.pool.execute(query, [hash]);

        if (rows.length > 0){
            rows[0].line_text = await decompressString(rows[0].line_text);
            return rows[0];
        } else {
            throw new ItemNotFoundError("Could not find blob with provided hash.");
        }
    }

    async deleteCommit(commitHash){
        const query = `DELETE FROM commits WHERE hash = UNHEX(?)`;
        await this.pool.execute(query, [commitHash]);
    }

    async depricateBlob(hash){
        const query = `UPDATE blobs SET dependencies = dependencies - 1
        WHERE blob_hash = UNHEX(?)`;
        await this.pool.execute(query, [hash]);
    }

    async trimBlobs(){
        const query = `DELETE FROM blobs WHERE dependencies < 1`;
        await this.pool.execute(query);
    }

    async getParentCommit(hash){
        const query = `
        SELECT HEX(parent_hash) AS parent_hash FROM commits
        WHERE hash = UNHEX(?)`;
        const [rows] = await this.pool.execute(query, [hash]);

        if (rows.length > 0){
            return rows[0].parent_hash;
        } else {
            throw new ItemNotFoundError("Could not find commit with provided hash.");
        }
    }

    async getRecentCommits(num){
        const query = `
        SELECT f.uri
        FROM files f
        JOIN commits c ON f.current_commit_hash = c.hash
        ORDER BY c.commit_date DESC
        LIMIT ?`;
        const [rows] = await this.pool.execute(query, [num]);

        if (rows.length > 0){
            return rows;
        } else {
            return [];
        }
    }

    async getCommitHistory(uri){
        const query = `
        WITH RECURSIVE commit_history AS (
            SELECT
                HEX(c.hash) AS hash,
                HEX(c.parent_hash) AS parent_hash,
                c.author,
                c.commit_date,
                c.line_changes
            FROM files f JOIN commits c ON f.current_commit_hash = c.hash WHERE f.uri = ?

            UNION ALL

            SELECT
                HEX(c.hash) AS hash,
                HEX(c.parent_hash) AS parent_hash,
                c.author,
                c.commit_date,
                c.line_changes
            FROM commits c JOIN commit_history ch ON c.hash = UNHEX(ch.parent_hash)
        )
        SELECT * FROM commit_history;`;

        const [rows] = await this.pool.execute(query, [uri]);

        if (rows.length > 0){
            for(let row in rows) rows[row].line_changes = JSON.parse(rows[row].line_changes);
            return rows;
        } else {
            throw new ItemNotFoundError(`Could not find commit history for ${uri}.`);
        }
    }

    async renameFile(oldURI, newURI){
        const query = `UPDATE files SET uri = ? WHERE uri = ?`;
        await this.pool.execute(query, [newURI, oldURI]);
    }

    async blame(hash){
        const query = `SELECT author FROM commits WHERE hash = UNHEX(?)`;
        let [rows] = await this.pool.execute(query, [hash]);

        if (rows.length > 0){
            return rows[0].author;
        } else {
            throw new ItemNotFoundError("Could not find commit with provided hash.");
        }
    }
}
